import './home.css'
import Navbar from '../navbar/Navbar'

import React, { useEffect, useState } from 'react'

function Home() {

  const [product,setProduct] = useState([])

  useEffect(()=>{
    fetch('https://dummyjson.com/products')
    .then(res =>{
      res = res.json().then((res)=>{
        setProduct(res.products)
        console.log(res.products);
      })
    })
  },[])


  return (
      <div>
        <Navbar heading={'Home page'} />
        <div  >
          {
            product?.map((product, idx)=>{
              return(
                  <div key={idx} className="card" style={{width:"18rem"}}>
                    <img src={product.thumbnail} className="card-img-top" alt={`image of ${product}`} />
                    <div className="card-body">
                      <h5 className="card-title">{product.title}</h5>
                      <p className="card-text">{product.description}</p>
                      <p className="card-text">Price: $<span style={{fontWeight:500}} >{product.price}</span></p>
                      <a href="#" className="btn btn-primary">Go somewhere</a>
                    </div>
                  </div>
              )
            })
          }
        </div>
      </div>
  )
}

export default Home
