import logo from './logo.svg';
import './App.css';

import axios from 'axios';
import { useEffect, useState } from 'react';


function App() {

  const [details,setDetails] = useState(null)
  const [name,setName] = useState()
  const [department,setDepartment] = useState()

  useEffect(()=>{
   getDetails()

  },[])

  const postDetails = (value) =>{
    axios.create({baseURL:'http://localhost:8000'}).post('',
      {
        "name": String(name),"department":String(department)
      }
    )
  }

  const getDetails = () =>{
    let fetchData = axios.get('http://localhost:8000').then((response)=>{
      setDetails(response.data)
      console.log(response.data);
    }).catch(err=>alert(err))
  }

  const handleSubmit = (e) =>{
    e.preventDefault()
    postDetails().then(()=>{

      getDetails()
    })
  }

  return (
    <div className="App">
      <h1>Hello world</h1>
      <form onSubmit={handleSubmit} >
        <label htmlFor='nameField' >Name </label>
        <input onChange={(e)=>setName(e.target.value)} id='nameField' type="text" placeholder='Type name here' ></input>
        <br/>
        <br/>
        <label  htmlFor='departmentField' >Department </label>
        <input  onChange={(e)=>setDepartment(e.target.value)} id='departmentField' type="text" placeholder='Type department here' ></input>
        <br/>
        <br/>
        <button type='submit' >Add record</button>
      </form>
      { details &&
         details.map((value,idx)=>{
          return (

            <h2 key={idx} >{value.name}, {value.department}</h2>
          )
        })
      }
    </div>
  );
}

export default App;
