  const [input,setInput] = useState({name:'newEmployee',department:"newDpartment"})



  const postDetails = () =>{
    axios.create({baseURL:'http://localhost:8000'}).post('',
      {
        "name":"hello","department":"haihello"
      }
    )
  }

  let fetchData = axios.get('http://localhost:8000').then((response)=>{
    setDetails(response.data)
  }).catch(err=>alert(err))

  return (
    <div className="App">
      <h1>Hello world</h1>
      <form>
        <label htmlFor='nameField' >Name </label>
        <input id='nameField' type="text" placeholder='Type name here' ></input>

        <label htmlFor='departmentField' >Name </label>
        <input id='nameField1' type="text" placeholder='Type name here' ></input>

        <button type='submit' onClick={postDetails} >Add record</button>
      </form>
      { details &&
         details.map((value,idx)=>{
          return (

            <h2 key={idx} >{value.name}, {value.department}</h2>
          )
        })
      }
    </div>
  );
}

export default App;
